package com.vince.math;

public class MathDemo
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		System.out.println("PI的值："+Math.PI);
		System.out.println("-10的绝对值："+Math.abs(-10));
		System.out.println("Math.random()="+Math.random());
		System.out.println("Math.round(98.56)="+Math.round(98.568977d));//round四舍五入
		System.out.println("Math.round(88.56789)="+Math.round(88.56789*100)/100.0);//保留小数.0
		System.out.println("Math.sqrt(2d)="+Math.sqrt(2d));
		
	}

}
